import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { useSocket } from '../context/SocketContext';

const HomeContainer = styled.div`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 24px;
  background: linear-gradient(to right, #f0f2f5, #ffffff);
`;

const Card = styled.div`
  background: #ffffff;
  border-radius: 16px;
  padding: 48px 40px;
  box-shadow: 0 10px 40px rgba(0, 0, 0, 0.1);
  width: 100%;
  max-width: 540px;
  text-align: center;
`;

const Title = styled.h1`
  color: #1e1e2f;
  margin-bottom: 12px;
  font-size: 2.4rem;
  font-weight: 700;
`;

const Subtitle = styled.p`
  color: #5c5c7a;
  margin-bottom: 32px;
  font-size: 1.05rem;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const InputGroup = styled.div`
  display: flex;
  flex-direction: column;
  text-align: left;
  gap: 8px;
`;

const Label = styled.label`
  color: #2f2f40;
  font-size: 0.95rem;
  font-weight: 600;
`;

const Input = styled.input`
  padding: 12px 16px;
  font-size: 1rem;
  border: 2px solid #dce3ec;
  border-radius: 8px;
  background: #f9f9fb;
  transition: border-color 0.25s;

  &:focus {
    border-color: #4f46e5;
    outline: none;
    background: #ffffff;
  }
`;

const CheckboxGroup = styled.div`
  display: flex;
  align-items: center;
  gap: 12px;
  margin-top: 8px;
`;

const Checkbox = styled.input`
  width: 18px;
  height: 18px;
  accent-color: #4f46e5;
`;

const Button = styled.button`
  background: linear-gradient(135deg, #4f46e5 0%, #6d28d9 100%);
  color: white;
  padding: 14px;
  font-size: 1.05rem;
  font-weight: 600;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 24px rgba(79, 70, 229, 0.25);
  }

  &:disabled {
    opacity: 0.6;
    cursor: not-allowed;
    box-shadow: none;
    transform: none;
  }
`;

const Divider = styled.div`
  margin: 30px 0;
  display: flex;
  align-items: center;
  gap: 12px;
  font-size: 0.95rem;
  color: #999;

  &::before,
  &::after {
    content: '';
    flex: 1;
    height: 1px;
    background: #e4e7ed;
  }
`;

const QuickJoinButton = styled.button`
  background: #ffffff;
  color: #333;
  padding: 12px 24px;
  border: 2px solid #dcdce7;
  border-radius: 8px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    background: #f0f2f7;
    border-color: #4f46e5;
  }
`;

const StatusIndicator = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 10px;
  margin-bottom: 24px;
  padding: 10px 16px;
  border-radius: 8px;
  background: ${props => props.connected ? '#e6f4ea' : '#fdecea'};
  color: ${props => props.connected ? '#256029' : '#8a1c1c'};
  font-size: 0.9rem;
`;

const StatusDot = styled.div`
  width: 10px;
  height: 10px;
  border-radius: 50%;
  background: ${props => props.connected ? '#28a745' : '#dc3545'};
`;

function Home() {
  const navigate = useNavigate();
  const { isConnected, joinRoom } = useSocket();
  const [mode, setMode] = useState('join');

  const [createData, setCreateData] = useState({
    username: '',
    isPrivate: false,
    password: '',
    passwordConfirm: ''
  });

  const [joinData, setJoinData] = useState({
    username: '',
    roomId: '',
    password: ''
  });

  const handleCreateChange = (e) => {
    const { name, value, type, checked } = e.target;
    setCreateData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleJoinChange = (e) => {
    const { name, value } = e.target;
    setJoinData(prev => ({ ...prev, [name]: value }));
  };

  const handleCreateSubmit = async (e) => {
    e.preventDefault();
    if (!createData.username.trim()) {
      alert('Please enter a username');
      return;
    }
    if (createData.isPrivate) {
      if (!createData.password.trim()) {
        alert('Please enter a password for private room');
        return;
      }
      if (createData.password !== createData.passwordConfirm) {
        alert('Passwords do not match');
        return;
      }
    }
    try {
      const res = await fetch(
        (process.env.REACT_APP_SOCKET_URL || 'http://localhost:5000') + '/api/rooms',
        {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            name: `Room ${Math.random().toString(36).substring(2, 10)}`,
            isPrivate: createData.isPrivate,
            password: createData.isPrivate ? createData.password : ''
          })
        }
      );
      if (!res.ok) {
        const err = await res.json();
        alert(err.message || 'Failed to create room');
        return;
      }
      const room = await res.json();
      localStorage.setItem('username', createData.username);
      joinRoom(room.roomId, createData.username, createData.isPrivate, createData.password);
      navigate(`/whiteboard/${room.roomId}`);
    } catch (error) {
      alert('Failed to create room');
    }
  };

  const handleJoinSubmit = (e) => {
    e.preventDefault();
    if (!joinData.username.trim()) {
      alert('Please enter a username');
      return;
    }
    if (!joinData.roomId.trim()) {
      alert('Please enter a Room ID');
      return;
    }
    localStorage.setItem('username', joinData.username);
    joinRoom(joinData.roomId.trim(), joinData.username, !!joinData.password, joinData.password);
    navigate(`/whiteboard/${joinData.roomId.trim()}`);
  };

  const handleQuickJoin = () => {
    const username = `User${Math.floor(Math.random() * 1000)}`;
    const roomId = generateRoomId();
    joinRoom(roomId, username);
    navigate(`/whiteboard/${roomId}`);
  };

  const generateRoomId = () => {
    return Math.random().toString(36).substring(2, 10);
  };

  return (
    <HomeContainer>
      <Card>
        <StatusIndicator connected={isConnected}>
          <StatusDot connected={isConnected} />
          {isConnected ? 'Connected to server' : 'Connecting to server...'}
        </StatusIndicator>
        <Title>Collaborative Whiteboard</Title>
        <Subtitle>Draw, collaborate, and create together in real-time</Subtitle>
        <div style={{ display: 'flex', gap: 16, justifyContent: 'center', marginBottom: 24 }}>
          <Button onClick={() => setMode('join')}>Join Room</Button>
          <Button onClick={() => setMode('create')}>Create Room</Button>
        </div>
        {mode === 'join' ? (
          <Form onSubmit={handleJoinSubmit}>
            <InputGroup>
              <Label htmlFor="join-username">Your Name</Label>
              <Input
                type="text"
                id="join-username"
                name="username"
                value={joinData.username}
                onChange={handleJoinChange}
                placeholder="Enter your name"
                required
              />
            </InputGroup>
            <InputGroup>
              <Label htmlFor="roomId">Room ID</Label>
              <Input
                type="text"
                id="roomId"
                name="roomId"
                value={joinData.roomId}
                onChange={handleJoinChange}
                placeholder="Enter Room ID"
                required
              />
            </InputGroup>
            <InputGroup>
              <Label htmlFor="join-password">Room Password (if private)</Label>
              <Input
                type="password"
                id="join-password"
                name="password"
                value={joinData.password}
                onChange={handleJoinChange}
                placeholder="Enter room password"
              />
            </InputGroup>
            <Button type="submit" disabled={!isConnected}>Join Room</Button>
          </Form>
        ) : (
          <Form onSubmit={handleCreateSubmit}>
            <InputGroup>
              <Label htmlFor="create-username">Your Name</Label>
              <Input
                type="text"
                id="create-username"
                name="username"
                value={createData.username}
                onChange={handleCreateChange}
                placeholder="Enter your name"
                required
              />
            </InputGroup>
            <CheckboxGroup>
              <Checkbox
                type="checkbox"
                id="isPrivate"
                name="isPrivate"
                checked={createData.isPrivate}
                onChange={handleCreateChange}
              />
              <Label htmlFor="isPrivate">Private Room</Label>
            </CheckboxGroup>
            {createData.isPrivate && (
              <>
                <InputGroup>
                  <Label htmlFor="create-password">Room Password</Label>
                  <Input
                    type="password"
                    id="create-password"
                    name="password"
                    value={createData.password}
                    onChange={handleCreateChange}
                    placeholder="Enter room password"
                    required
                  />
                </InputGroup>
                <InputGroup>
                  <Label htmlFor="create-password-confirm">Confirm Password</Label>
                  <Input
                    type="password"
                    id="create-password-confirm"
                    name="passwordConfirm"
                    value={createData.passwordConfirm}
                    onChange={handleCreateChange}
                    placeholder="Confirm room password"
                    required
                  />
                </InputGroup>
              </>
            )}
            <Button type="submit" disabled={!isConnected}>Create Room</Button>
          </Form>
        )}
        <Divider>OR</Divider>
        <QuickJoinButton onClick={handleQuickJoin}>Quick Join as Guest</QuickJoinButton>
      </Card>
    </HomeContainer>
  );
}

export default Home;
