import React, { useRef, useEffect, useImperativeHandle, forwardRef } from 'react';

const CANVAS_WIDTH = 1200;
const CANVAS_HEIGHT = 700;

const Canvas = forwardRef(({
  tool = 'pen',
  color = '#000000',
  brushSize = 2,
  opacity = 1,
  onDrawEvent,
  onDrawStart,
  onDrawEnd,
  remoteDrawEvent
}, ref) => {
  const canvasRef = useRef(null);
  const ctxRef = useRef(null);
  const drawing = useRef(false);
  const startPoint = useRef(null);
  const snapshot = useRef(null);
  const localUndoStack = useRef([]);
  const localRedoStack = useRef([]);
  const currentStroke = useRef([]);

  // Save current canvas state
  const saveCanvasState = () => {
    localUndoStack.current.push(
      ctxRef.current.getImageData(0, 0, CANVAS_WIDTH, CANVAS_HEIGHT)
    );
    localRedoStack.current = [];
  };

  useImperativeHandle(ref, () => ({
    exportImage: () => canvasRef.current.toDataURL('image/png'),
    exportPDF: async () => {
      const jsPDF = (await import('jspdf')).jsPDF;
      const pdf = new jsPDF({ orientation: 'landscape' });
      pdf.addImage(canvasRef.current.toDataURL('image/png'), 'PNG', 10, 10, 280, 160);
      return pdf;
    },
    clear: () => clearCanvas(),
    undo: () => handleUndo(),
    redo: () => handleRedo(),
    loadImageFromDataUrl: (dataUrl) => {
      const img = new Image();
      img.onload = () => {
        ctxRef.current.clearRect(0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);
        ctxRef.current.drawImage(img, 0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);
        saveCanvasState();
      };
      img.src = dataUrl;
    }
  }));

  useEffect(() => {
    const canvas = canvasRef.current;
    canvas.width = CANVAS_WIDTH;
    canvas.height = CANVAS_HEIGHT;
    ctxRef.current = canvas.getContext('2d');
    ctxRef.current.lineCap = 'round';
    ctxRef.current.lineJoin = 'round';
    ctxRef.current.globalAlpha = opacity;
    saveCanvasState();
  }, [opacity]);

  useEffect(() => {
    if (!remoteDrawEvent) return;

    const { type } = remoteDrawEvent;
    if (type === 'stroke') drawStroke(remoteDrawEvent.points, remoteDrawEvent);
    else if (type === 'draw') drawLine(remoteDrawEvent.prev, remoteDrawEvent.current, remoteDrawEvent);
    else if (type === 'shape') drawShape(remoteDrawEvent);
    else if (type === 'text') drawText(remoteDrawEvent);
    else if (type === 'clear') clearCanvas();
  }, [remoteDrawEvent]);

  const getPointer = (e) => {
    const rect = canvasRef.current.getBoundingClientRect();
    const scaleX = canvasRef.current.width / rect.width;
    const scaleY = canvasRef.current.height / rect.height;
    return {
      x: (e.clientX - rect.left) * scaleX,
      y: (e.clientY - rect.top) * scaleY
    };
  };

  const handlePointerDown = (e) => {
    drawing.current = true;
    const point = getPointer(e);
    startPoint.current = point;
    snapshot.current = ctxRef.current.getImageData(0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);
    currentStroke.current = [point];

    if (tool === 'pen' || tool === 'eraser') {
      ctxRef.current.beginPath();
      ctxRef.current.moveTo(point.x, point.y);
      ctxRef.current.strokeStyle = tool === 'eraser' ? '#fff' : color;
      ctxRef.current.lineWidth = brushSize;
      ctxRef.current.globalAlpha = opacity;
      if (onDrawStart) onDrawStart(point);
    }
  };

  const handlePointerMove = (e) => {
    if (!drawing.current) return;
    const point = getPointer(e);

    if (tool === 'pen' || tool === 'eraser') {
      ctxRef.current.lineTo(point.x, point.y);
      ctxRef.current.stroke();
      currentStroke.current.push(point);
    } else if (tool === 'rectangle' || tool === 'circle') {
      ctxRef.current.putImageData(snapshot.current, 0, 0);
      ctxRef.current.strokeStyle = color;
      ctxRef.current.lineWidth = brushSize;
      ctxRef.current.globalAlpha = opacity;

      if (tool === 'rectangle') {
        ctxRef.current.strokeRect(
          startPoint.current.x,
          startPoint.current.y,
          point.x - startPoint.current.x,
          point.y - startPoint.current.y
        );
      } else {
        const radius = Math.hypot(point.x - startPoint.current.x, point.y - startPoint.current.y);
        ctxRef.current.beginPath();
        ctxRef.current.arc(startPoint.current.x, startPoint.current.y, radius, 0, Math.PI * 2);
        ctxRef.current.stroke();
      }
    }
  };

  const handlePointerUp = (e) => {
    if (!drawing.current) return;
    drawing.current = false;
    const point = getPointer(e);

    if (tool === 'rectangle' || tool === 'circle') {
      handlePointerMove(e); // draw shape
      saveCanvasState();
      if (onDrawEvent) {
        onDrawEvent({
          type: 'shape',
          shape: tool,
          start: startPoint.current,
          end: point,
          color,
          brushSize,
          opacity,
          canvasData: canvasRef.current.toDataURL()
        });
      }
    }

    if ((tool === 'pen' || tool === 'eraser') && currentStroke.current.length > 1) {
      saveCanvasState();
      if (onDrawEvent) {
        onDrawEvent({
          type: 'stroke',
          points: currentStroke.current,
          tool,
          color,
          brushSize,
          opacity,
          canvasData: canvasRef.current.toDataURL()
        });
      }
    }

    currentStroke.current = [];
    if (onDrawEnd) onDrawEnd(point);
  };

  const handleDoubleClick = (e) => {
    if (tool === 'text') {
      const point = getPointer(e);
      const text = prompt('Enter text:');
      if (text) {
        ctxRef.current.font = `${brushSize * 6}px Arial`;
        ctxRef.current.fillStyle = color;
        ctxRef.current.globalAlpha = opacity;
        ctxRef.current.fillText(text, point.x, point.y);
        saveCanvasState();
        if (onDrawEvent) {
          onDrawEvent({
            type: 'text',
            text,
            point,
            color,
            brushSize,
            opacity,
            canvasData: canvasRef.current.toDataURL()
          });
        }
      }
    }
  };

  const handleUndo = () => {
    if (localUndoStack.current.length > 1) {
      localRedoStack.current.push(localUndoStack.current.pop());
      const prev = localUndoStack.current[localUndoStack.current.length - 1];
      ctxRef.current.putImageData(prev, 0, 0);
    }
  };

  const handleRedo = () => {
    if (localRedoStack.current.length > 0) {
      const redoImg = localRedoStack.current.pop();
      ctxRef.current.putImageData(redoImg, 0, 0);
      localUndoStack.current.push(redoImg);
    }
  };

  const clearCanvas = () => {
    ctxRef.current.clearRect(0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);
    saveCanvasState();
  };

  const drawLine = (from, to, settings) => {
    ctxRef.current.save();
    ctxRef.current.beginPath();
    ctxRef.current.moveTo(from.x, from.y);
    ctxRef.current.lineTo(to.x, to.y);
    ctxRef.current.strokeStyle = settings.tool === 'eraser' ? '#fff' : settings.color;
    ctxRef.current.lineWidth = settings.brushSize;
    ctxRef.current.globalAlpha = settings.opacity;
    ctxRef.current.stroke();
    ctxRef.current.restore();
  };

  const drawStroke = (points, settings) => {
    if (!points || points.length < 2) return;
    ctxRef.current.save();
    ctxRef.current.beginPath();
    ctxRef.current.moveTo(points[0].x, points[0].y);
    for (let i = 1; i < points.length; i++) {
      ctxRef.current.lineTo(points[i].x, points[i].y);
    }
    ctxRef.current.strokeStyle = settings.tool === 'eraser' ? '#fff' : settings.color;
    ctxRef.current.lineWidth = settings.brushSize;
    ctxRef.current.globalAlpha = settings.opacity;
    ctxRef.current.stroke();
    ctxRef.current.restore();
  };

  const drawShape = ({ shape, start, end, color, brushSize, opacity }) => {
    ctxRef.current.save();
    ctxRef.current.strokeStyle = color;
    ctxRef.current.lineWidth = brushSize;
    ctxRef.current.globalAlpha = opacity;

    if (shape === 'rectangle') {
      ctxRef.current.strokeRect(start.x, start.y, end.x - start.x, end.y - start.y);
    } else if (shape === 'circle') {
      const radius = Math.hypot(end.x - start.x, end.y - start.y);
      ctxRef.current.beginPath();
      ctxRef.current.arc(start.x, start.y, radius, 0, 2 * Math.PI);
      ctxRef.current.stroke();
    }

    ctxRef.current.restore();
  };

  const drawText = ({ text, point, color, brushSize, opacity }) => {
    ctxRef.current.save();
    ctxRef.current.font = `${brushSize * 6}px Arial`;
    ctxRef.current.fillStyle = color;
    ctxRef.current.globalAlpha = opacity;
    ctxRef.current.fillText(text, point.x, point.y);
    ctxRef.current.restore();
  };

  return (
    <div style={{ position: 'relative', display: 'inline-block' }}>
      <canvas
        ref={canvasRef}
        width={CANVAS_WIDTH}
        height={CANVAS_HEIGHT}
        style={{
          background: '#fff',
          borderRadius: 16,
          boxShadow: '0 4px 16px rgba(0,0,0,0.07)',
          margin: 24,
          cursor: tool === 'text' ? 'text' : tool === 'eraser' ? 'cell' : 'crosshair',
          touchAction: 'none',
          userSelect: 'none',
          display: 'block',
          width: '100%',
          maxHeight: '80vh',
          objectFit: 'contain'
        }}
        onPointerDown={handlePointerDown}
        onPointerMove={handlePointerMove}
        onPointerUp={handlePointerUp}
        onDoubleClick={handleDoubleClick}
      />
    </div>
  );
});

export default Canvas;
