//   color: #777;
// `;

const EmptyIcon = styled.div`
  font-size: 2.5rem;
  margin-bottom: 12px;
  opacity: 0.5;
`;

const TypingIndicator = styled.div`
  font-size: 0.8rem;
  color: #aab4f9;
  font-style: italic;
  padding-left: 10px;
  margin-bottom: 6px;
  transition: all 0.2s;
`;

function Sidebar({ users, roomId, currentUsername, onClose, onNewMessage }) {
  const [activeTab, setActiveTab] = useState('chat');
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [typingUsers, setTypingUsers] = useState(new Set());
  const { socket, sendMessage } = useSocket();
  const messagesEndRef = useRef(null);
  const typingTimeoutRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    if (socket) {
      // Handle incoming messages
      socket.on('receive-message', (messageData) => {
        setMessages(prev => {
          // Only call onNewMessage if this is a new message
          if (onNewMessage && prev.length > 0) {
            onNewMessage();
          }
          return [...prev, messageData];
        });
      });

      // Handle room joined event (load existing messages)
      socket.on('room-joined', (data) => {
        if (data.messages) {
          setMessages(data.messages);
        }
      });

      // Handle typing indicators
      socket.on('user-typing', (data) => {
        if (data.username !== currentUsername) {
          setTypingUsers(prev => new Set(prev).add(data.username));
        }
      });

      socket.on('user-stop-typing', (data) => {
        if (data.username !== currentUsername) {
          setTypingUsers(prev => {
            const newSet = new Set(prev);
            newSet.delete(data.username);
            return newSet;
          });
        }
      });

      return () => {
        socket.off('receive-message');
        socket.off('room-joined');
        socket.off('user-typing');
        socket.off('user-stop-typing');
      };
    }
  }, [socket, currentUsername, onNewMessage]);

  const handleSendMessage = () => {
    if (newMessage.trim() && sendMessage) {
      sendMessage(newMessage.trim(), currentUsername, roomId);
      setNewMessage('');
      setIsTyping(false);
      
      // Clear typing indicator
      if (socket) {
        socket.emit('stop-typing', { roomId, username: currentUsername });
      }
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleInputChange = (e) => {
    setNewMessage(e.target.value);
    
    // Handle typing indicators
    if (!isTyping) {
      setIsTyping(true);
      if (socket) {
        socket.emit('typing', { roomId, username: currentUsername });
      }
    }

    // Clear existing timeout
    if (typingTimeoutRef.current) {
      clearTimeout(typingTimeoutRef.current);
    }

    // Set new timeout
    typingTimeoutRef.current = setTimeout(() => {
      setIsTyping(false);
      if (socket) {
        socket.emit('stop-typing', { roomId, username: currentUsername });
      }
    }, 1000);
  };

  const formatTime = (timestamp) => {
    const date = new Date(timestamp);
    const now = new Date();
    const diffInHours = (now - date) / (1000 * 60 * 60);
    
    if (diffInHours < 24) {
      return date.toLocaleTimeString([], { 
        hour: '2-digit', 
        minute: '2-digit' 
      });
    } else {
      return date.toLocaleDateString([], {
        month: 'short',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      });
    }
  };

  const getInitials = (username) => {
    return username
      .split(' ')
      .map(word => word.charAt(0))
      .join('')
      .toUpperCase()
      .slice(0, 2);
  };

  const renderTypingIndicator = () => {
    if (typingUsers.size === 0) return null;
    
    const users = Array.from(typingUsers);
    let text = '';
    
    if (users.length === 1) {
      text = `${users[0]} is typing...`;
    } else if (users.length === 2) {
      text = `${users[0]} and ${users[1]} are typing...`;
    } else {
      text = `${users[0]} and ${users.length - 1} others are typing...`;
    }
    
    return <TypingIndicator>{text}</TypingIndicator>;
  };

  return (
    <SidebarContainer>
      <TabContainer>
        <Tab 
          $active={activeTab === 'chat'} 
          onClick={() => setActiveTab('chat')}
        >
          Chat ({messages.length})
        </Tab>
        <Tab 
          $active={activeTab === 'users'} 
          onClick={() => setActiveTab('users')}
        >
          Users ({users.length})
        </Tab>
        <CloseButton onClick={onClose}/>
      </TabContainer>

      <ContentArea>
        {activeTab === 'chat' && (
          <ChatContainer>
            <MessagesContainer>
              {messages.length === 0 ? (
                <EmptyState>
                  <EmptyIcon>ðŸ’¬</EmptyIcon>
                  <div>No messages yet</div>
                  <div style={{ fontSize: '0.8rem', marginTop: '8px' }}>
                    Start the conversation!
                  </div>
                </EmptyState>
              ) : (
                <>
                  {messages.map((message, index) => (
                    <Message 
  key={index} 
  $isOwn={message.username !== currentUsername}
  style={{ 
    alignSelf: message.username !== currentUsername ? 'flex-start' : 'flex-end',
    marginLeft: message.username !== currentUsername ? '0':'auto'
  }}
>
                      <MessageHeader>
                        <span>{message.username}</span>
                        <span>{formatTime(message.timestamp)}</span>
                      </MessageHeader>
                      <MessageContent>{message.message}</MessageContent>
                    </Message>
                  ))}
                  {renderTypingIndicator()}
                  <div ref={messagesEndRef} />
                </>
              )}
            </MessagesContainer>
            
            <ChatInputContainer>
              <ChatInput
                type="text"
                value={newMessage}
                onChange={handleInputChange}
                onKeyPress={handleKeyPress}
                placeholder="Type a message..."
                maxLength={500}
              />
              <SendButton 
                onClick={handleSendMessage}
                disabled={!newMessage.trim()}
              >
                Send
              </SendButton>
            </ChatInputContainer>
          </ChatContainer>
        )}

        {/* {activeTab === 'users' && (
          <UsersContainer>
            {users.length === 0 ? (
              <EmptyState>
                <EmptyIcon>ðŸ‘¥</EmptyIcon>
                <div>No users online</div>
                <div style={{ fontSize: '0.8rem', marginTop: '8px' }}>
                  You're the first one here!
                </div>
              </EmptyState>
            ) : (
              users.map((user, index) => (
                <UserItem key={user.socketId || index}>
                  <UserAvatar>
                    {getInitials(user.username)}
                  </UserAvatar>
                  <UserInfo>
                    <Username>
                      {user.username}
                      {user.username === currentUsername && ' (You)'}
                    </Username>
                    <UserStatus>
                      Online â€¢ Joined {formatTime(user.joinedAt || Date.now())}
                    </UserStatus>
                  </UserInfo>
                  <OnlineIndicator />
                </UserItem>
              ))
            )}
          </UsersContainer> */}
        {/* )} */}
      </ContentArea>
    </SidebarContainer>
  );
}

export default Sidebar;
